function buildJob(o){var i="";i+="<div class='row jl-today-row'>",i+="<div class='col-sm-2 col-xs-2 jl-time'>",i+=o.time,i+="</div>",i+="<h2 class='col-sm-10 col-xs-10'>",i+=o.name,i+="</h2>",i+="</div>",$("#todays-jobs").append(i)}function buildTodaysJobs(o){$(o).each(function(){buildJob(this)})}var jobs=[];jobs.push({name:"Example Job",time:"9:00"}),$(document).ready(function(){buildTodaysJobs(jobs),$("#jobSubmit").click(function(){buildJob({name:$("#jobName").val(),time:$("#jobTime").val()})})});